import React, { useEffect, useState } from "react";
import { getCommentReviewsByProductId } from "../../../utils/connectDB";
import Spinner from "../../UI/spinner/spinner.component";
import {
  CommentReviewsWrapper,
  TextArea,
  CommentsWrapper,
} from "./comment-reviews.styles";
import CommentReviewsItem from "../comment-reviews-item/comment-reviews-item.component";
import Button from "@material-ui/core/Button";
const CommentReviews = ({ show, productId }) => {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  useEffect(() => {
    let _mounted = true;
    if (productId) {
      setLoading(true);
      getCommentReviewsByProductId(productId)
        .then((data) => {
          if (_mounted) {
            setComments(data);
            setLoading(false);
          }
        })
        .catch((err) => {
          if (_mounted) {
            setError(err);
            setLoading(false);
          }
        });
    }
    return () => {
      _mounted = false;
    };
  }, [productId]);
  console.log(comments);
  return (
    <CommentReviewsWrapper>
      {loading ? (
        <Spinner loadChildComponent />
      ) : (
        <React.Fragment>
          <TextArea placeholder="Để lại bình luận của bạn tại đây" />
          <Button color="primary" variant="contained" size="small">
            Gửi
          </Button>
          <CommentsWrapper>
            { comments.map((comment) => {
                  console.log(comment);
                  return (
                    <CommentReviewsItem key={comment._id} comment={comment} />
                  );
                })
              }
          </CommentsWrapper>
        </React.Fragment>
      )}
    </CommentReviewsWrapper>
  );
};

export default CommentReviews;
