import React from "react";
import SignIn from "../../components/Auth/signin/signin.component";
import SignUp from "../../components/Auth/signup/signup.component";
import RestoreAccount from "../../Auth/components/forgot-password/forgot-password.component";
import ForgotPasswordDone from "../../components/forgot-password-done/forgot-password-done.component";
import { Switch, Route } from "react-router-dom";
import { Redirect } from "react-router-dom";

class AuthPage extends React.Component {
  render() {
    const { match, location, currentUser } = this.props;
    if (currentUser) {
      if (location.state) return <Redirect to={location.state.from} />;
      return <Redirect to="/" />;
    }
    return (
      <Switch>
        <Route
          path={`${match.path}/signin`}
          render={(props) => <SignIn authPath={match.path} {...props} />}
        />
        <Route
          path={`${match.path}/signup`}
          render={(props) => <SignUp authPath={match.path} {...props} />}
        />
        <Route
          path={`${match.path}/restore-account`}
          exact
          render={(props) => (
            <RestoreAccount authPath={match.path} {...props} />
          )}
        />
        <Route
          path={`${match.path}/restore-account`}
          component={ForgotPasswordDone}
        />
      </Switch>
    );
  }
}


export default AuthPage;
