  const LatestProductsGroup = [] ; 
  const accumulatorProducts = [];
  getTop10LatestProducts.forEach((product,id) => {
    if(id % 3 === 0 && id !== 0){
      LatestProductsGroup.push(accumulatorProducts);
      accumulatorProducts = [];
    }
    if(id === getTop10LatestProducts.length - 1 ){
      LatestProductsGroup.push(accumulatorProducts)
    }
    accumulatorProducts = [];
  })
  return (
    <LatestProductsContainer>
      <Title>sản phẩm gần đây</Title>
      <Slider>

      </Slider>
    </LatestProductsContainer>
  )
}

export default LatestProducts
