import React, { useState, useEffect } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Select,
  Option,
  Button,
} from "./edit-user-form.styles";
import { connect } from "react-redux";
import { updateUserInfoStart } from "../../../redux/user/user.actions";
import { selectUserError } from "../../../redux/user/user.selectors";
const CheckoutForm = ({ currentUser, updateUserInfo }) => {
  const handleUpdateUserInfo = () => {};
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [city, setCity] = useState("");
  const [district, setDistrict] = useState("");
  const [ward, setWard] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [disabledBtn, setDisabledBtn] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    if (
      !firstName ||
      !lastName ||
      !city ||
      !district ||
      !ward ||
      !address ||
      !phone ||
      !email
    )
      return setDisabledBtn(true);
    setDisabledBtn(false);
  }, [firstName, lastName, city, district, ward, address, phone, email]);
  useEffect(() => {
    if (currentUser && currentUser.information) {
      setFirstName(currentUser.information.first_name);
      setLastName(currentUser.information.last_name);
      setCity(currentUser.information.city);
      setDistrict(currentUser.information.district);
      setWard(currentUser.information.ward);
      setAddress(currentUser.information.address);
      setPhone(currentUser.information.phone);
      setEmail(currentUser.information.email);
    }
  }, [currentUser]);

  const handleSubmitUpdateForm = (e) => {
    e.preventDefault();
    if (
      !firstName ||
      !lastName ||
      !city ||
      !district ||
      !ward ||
      !address ||
      !phone ||
      !email
    ) {
      return setError("Bạn phải điền tất cả các trường")
    }
    const userInformation = {
      first_name : firstName, 
      last_name : lastName, 
      city,
      district , 
      ward, 
      address, 
      phone, 
      email
    }
    updateUserInfo(userInformation)
  };
  return (
    <Form onSubmit={handleSubmitUpdateForm}>
      {error ? <h4>{error}</h4> : null}
      <FormInline>
        <FormGroup>
          <Label htmlFor="fname">
            Họ<Required>*</Required>
          </Label>
          <Input
            name="fname"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label htmlFor="lname">
            Tên<Required>*</Required>
          </Label>
          <Input
            name="lname"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </FormGroup>
      </FormInline>
      <FormGroup>
        <Label htmlFor="city">Tỉnh/ Thành phố</Label>
        <Input
          name="city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
      </FormGroup>
      <FormGroup>
        <Label htmlFor="district">Quận/ Huyện</Label>
        <Input
          name="district"
          value={district}
          onChange={(e) => setDistrict(e.target.value)}
        />
      </FormGroup>
      <FormGroup>
        <Label htmlFor="ward">Phường/ Xã</Label>
        <Input
          name="ward"
          value={ward}
          onChange={(e) => setWard(e.target.value)}
        />
      </FormGroup>
      <FormGroup>
        <Label htmlFor="address">Số nhà, đường</Label>
        <Input
          name="address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
      </FormGroup>
      <FormInline>
        <FormGroup>
          <Label htmlFor="phone">Số điện thoại</Label>
          <Input
            name="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label htmlFor="email">Email</Label>
          <Input
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </FormGroup>
      </FormInline>
      {/* <Select >
        <Option value="#" disabled selected hidden>Phương thức thanh toán</Option>
        <Option value="cod">Tiền mặt (COD)</Option>
        <Option value="paypal">Thanh toán qua thẻ</Option>
      </Select> */}
      <Button
        style={{ display: "block", margin: "0 auto" }}
        onClick={handleUpdateUserInfo}
        disabled={disabledBtn}
      >
        Lưu thay đổi
      </Button>
    </Form>
  );
};

const mapDispatchToProps = (dispatch) => ({
  updateUserInfo: (userInfo) => dispatch(updateUserInfoStart(userInfo)),
});

export default connect(null, mapDispatchToProps)(CheckoutForm);
