import React from 'react'
import {ProductBestSellerContainer, Title} from "./product-best-seller.styles";
import {getProductsBestSeller} from "../../utils/algorithms";
import Slider from "react-slick"
const productsBestSeller = getProductsBestSeller();
let productBestSellerGroup = [];
let accumulatorProducts = [];
productsBestSeller.forEach( (product,idx) => {
  if( (idx !== 0 && idx % 3 === 0 )  ){
    productBestSellerGroup.push(accumulatorProducts);
    accumulatorProducts = [];
  }    
  accumulatorProducts.push(product)
  if( idx %3 !==0 && idx === productsBestSeller.length -1 ){
    productBestSellerGroup.push(accumulatorProducts);
    accumulatorProducts = [];
  }
})
const ProductsBestSeller = () => {
  console.log(productBestSellerGroup)
  return (
    <ProductBestSellerContainer>
    <Title>sản phẩm bán chạy</Title>
    <Slider {...settings} style={{height: "85%"}}>
      {latestProductsGroup.map((groups, id) => (
        <Grid key={id}>
          {groups.map((product) => (
            <Slot key={product.userId}>
              <ProductImageContainer>
                <ProductImage src={product.imageUrl}/>
              </ProductImageContainer>
              <ProductLabel>
                 <ProductName >{product.name}</ProductName> 
                 {product.discount === 0 ? 
                  (<OriginalPrice>{product.price.toLocaleString("es-AR")} Đ</OriginalPrice>) : 
                  (
                    <React.Fragment>
                        <OriginalPrice discount>{product.price.toLocaleString("es-AR")} Đ</OriginalPrice>
                        <ProductDiscountPrice>{(product.price*(100-product.discount)/100).toLocaleString("es-AR")} Đ</ProductDiscountPrice>
                    </React.Fragment>
                  )
                 }
              </ProductLabel>
            </Slot>
          ))}
        </Grid>
      ))}
    </Slider>
  </ProductBestSellerContainer>
  )
}

export default ProductsBestSeller
