import styled , {keyframes} from "styled-components";


const loaderWrapper = keyframes`
  0%{
    opacity : 1; 
  }
  50%{
    opacity : 0 ; 
  }
  100%{
    opacity : 1 ;
  }
`
export const LoaderWrapper = styled.div`
  display : fixed ;
  left: 0 ;
  right : 0 ;
  top : 0; 
  bottom : 0;
  width : 100%;
  height : 100%;
  background-color : black; 
  z-index : 999
`
