import styled from "styled-components";
import {lighten} from "polished"
export const ProductProfileTextContainer = styled.div`
  width : 100%; 
  padding :1rem 3rem;
  border-left : 1px solid #eee;
  height : 100% ; 
`
export const Title = styled.h2`
  font-size : 1.6em ;
  font-weight : bold ;  
`
export const Reviews = styled.div`
  margin: 1rem 0;
  display : flex ; 
  align-items : center;
  font-size : 0.95em;
`
export const Span = styled.span`
  color : ${({color}) => color ? color : "inherit" };
`
export const Price = styled.h4`
  color : #dd2222;
  font-size : 1.4em;
`

export const BriefTextsInfo = styled.ul` 
  margin : 1rem;
  line-height : 1.6;
`

export const TextItem = styled.li``
export const  ProductActions = styled.div`
  display : flex ;   
  height : 3rem; 
  align-items : center;
  overflow : hidden ;
`
export const  ProductQuantity = styled.div`
  display : flex ; 
  font-size : 1.2em;  
  height : 100%;
`
export const  Button = styled.button`
  outline : none ; 
  border : none  ;
  padding : 1rem;
  background-color : ${({bgColor}) => bgColor ? bgColor : "#404040"};
  color : ${({bgColor}) => bgColor ? "black" : "white"};;
  &:hover{
    background-color : ${({bgColor}) => bgColor ? lighten("0.1",bgColor ) :  lighten("0.1","#404040")};
  }
`
export const  Paragraph = styled.p`
  padding : 1rem;
`
