import styled from "styled-components";
import NumberFormat from "react-number-format";


export const FormCreateProductWrapper = styled.div`
  margin : 5rem auto;
`

export const CustomNumberFormat = styled(NumberFormat)`
  visibility : ${({isDiscount}) => isDiscount ? "visible" : "hidden"};
  width : ${({isDiscount}) => isDiscount ? "100%" : 0};
  height : 2.5rem; 
  padding : .1rem;
  font-size : 1.1em;
  border : none ; 
  outline : none ; 
  border-bottom :1px solid #424242;
  &:focus { 
    border-bottom : 2px solid #002984;
  };
  transition : all 0.4s;
`
