import React from 'react'
import {ResponseItemWrapper, Avatar, Row, ResponseText} from "./response-item.styles";
const ReponseItem = ({response}) => {
  return (
    <ResponseItemWrapper>
      <Row>
        <Avatar src={`http://localhost:5000/images/${response.user.avatar}`} />
        <ResponseText>
            <p>
              <strong>
                {response.user.name}{" "}
                {response.user._id === product.user._id ? (
                  <span style={{ color: "#3f51b5" }}>
                    Nhà bán hàng <FcBusinessman />
                  </span>
                ) : (
                  ""
                )}
              </strong>{" "}
              <span style={{ color: "rgba(0,0,0,0.75)" }}>
                <Moment format="DD-MM-YYYY HH:mm">{comment.createdAt}</Moment>
              </span>{" "}
            </p>
            <p>
              {text}{" "}
              {showReadMore ? (
                <span>
                  {!readMore ? "..." : " " }
                  <ReadMore onClick={handleSetReadMore}>
                    {!readMore ? "Xem thêm" : "Thu gọn"}
                  </ReadMore>
                </span>
              ) : null}{" "}
            </p>
          </ResponseText>
      </Row>
    </ResponseItemWrapper>
  )
}

export default ReponseItem
