import styled from "styled-components";

export const Option = styled.span`
  padding: 0 1rem;
  width: 100%;
  position: relative;
  padding: ${({ isMobile }) => (isMobile ? "1rem 0" : "0")};  
`;

export const RowInline = styled.div`
  width: 100%;
  display: flex;
  & > * {
    display: flex;
    width: 50%;
  }
`;
