import React, {useState} from 'react'
import {Select, Option} from "./checkout-payment.styles";

const CheckoutPayment = ({history, location}) => {
  const [payMethod, setPayMethod] = useState("");

  if(location.state.from !== "/checkout"){
    return (
      <div>
        <h4>Rất tiếc, bạn chưa tiến hành checkout đơn hàng nên không thể truy cập địa chỉ này</h4>
        <button>Quay lại trang chủ</button>
      </div>
    )
  }  
  return (
      <Select value={payMethod}>
        <Option value="">Phương thức thanh toán</Option>
        <Option value="cod">Tiền mặt (COD)</Option>
        <Option value="paypal">Thanh toán qua thẻ</Option>
      </Select> 
  )
}

export default CheckoutPayment
