import React from 'react'
import {ShopGridLeftSideContainer} from "./product-type-left-side.styles";
import LesfSideMenu from "../../Layout/left-side-menu/left-side-menu.component";
import PriceScope from "../price-scope/price-scope.component";
import StarScope from "../star-scope/star-scope.component";
import {connect} from "react-redux";
import {createStructuredSelector} from "reselect";
import {selectProductGroupList, selectMaxPrice, selectManufactorList} from  "../../../redux/product-type/product-type.selectors";
import LeftSideMenu from '../../Layout/left-side-menu/left-side-menu.component';
const ShopGridLeftSide = ({mobileView, tabletView, productGroupList, maxPrice,manufactorList}) => {   
  return (
    <ShopGridLeftSideContainer>   
      {productGroupList.length && <LesfSideMenu title="DS Nhóm SP" list={productGroupList}/>}    
      {manufactorList.length && <LesfSideMenu title="DS Nhà SX" list={manufactorList}/>}          
      <PriceScope mobileView={mobileView} tabletView={tabletView} maxPrice={maxPrice}/>   
      <StarScope mobileView={mobileView} tabletView={tabletView}/>  
    </ShopGridLeftSideContainer>
  )
}

const mapStateToProps = createStructuredSelector({
  productGroupList : selectProductGroupList, 
  maxPrice : selectMaxPrice,
  manufactorList : selectManufactorList
})

export default connect(mapStateToProps)(ShopGridLeftSide)
