import styled from "styled-components";

export const ProductsPopupContainer = styled.div`
  position : fixed;  
  left : ${({offsetWidth}) => `calc(${offsetWidth}px -2%)` };
  padding : 1rem 2rem;
  display : flex ;
  top : 4rem;
  width : 1000px;
  height : 550px;
  background-color : white; 
  border : 1px solid #ccc;
  z-index: 500;
`
