import React, { useState } from "react";
import {
  ProductImagesContainer,
  BackgroundImageContainer,
  Image,
} from "./product-images.styles";
import Zoom from "react-img-zoom";
import Slider from "react-slick";
import GlassImage from "../../UI/magnifiers-image/glass-manifiers/glass-manifiers.component";
import SideManifiers from "../../UI/magnifiers-image/side-manifiers/side-manifiers.component";

const images = [
  require("../../../assets/img/blog/blog-1.jpg"),
  require("../../../assets/img/blog/blog-2.jpg"),
  require("../../../assets/img/blog/blog-3.jpg"),
  require("../../../assets/img/blog/blog-4.jpg"),
  require("../../../assets/img/blog/blog-5.jpg"),
  require("../../../assets/img/blog/blog-6.jpg"),
];

const ProductImages = () => {
  const [currentImg, setCurrentImg] = useState(require("../../../assets/img/sample-image.jpg"));
  React.useEffect(() => {
    console.log(currentImg);
  }, [currentImg]);
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplay: true,
    speed: 2000,
    autoplaySpeed: 2000,
    cssEase: "linear",
  };
  return (
    <ProductImagesContainer>
      <BackgroundImageContainer>
        <SideManifiers image={currentImg} largeImage={currentImg} width={400} height={300}/>
      </BackgroundImageContainer>
      <Slider {...settings}>
        {images.map((image, index) => (
          <Image key={image} src={image} onClick={() => setCurrentImg(image)} />
        ))}
      </Slider>
    </ProductImagesContainer>
  );
};

export default ProductImages;
