import React from "react";
import StripeCheckout from "react-stripe-checkout";
import { clearCartItems } from "../../../redux/cart/cart.actions";
import { connect } from "react-redux";
import { selectCurrentUser } from "../../../redux/user/user.selectors";
import {
  selectCartItems,
  selectTotalFeeShip,
  selectTotalPrice,
  selectFastDeliveryCost,
  selectMethodDelivery, 

} from "../../../redux/cart/cart.selectors";
import {postCardPayment} from "../../../utils/connectDB"
import { createStructuredSelector } from "reselect";
const StripeButton = ({userMessage, methodDelivery,  clearCartItems, currentUser, cartItems , totalPrice, totalFeeShip, fastDeliveryCost }) => {
  const publish_key =
    "pk_test_51H2ox9KqyLpFyVsNQPr7V1eFkeRTtEtdG7vgXAlM4E3UiDJ9U8MXv5RtLoo2qLMW6qckNvDbwf5PcZXmNfbAJhQd00zyZ5D9ir";
  const priceForStripe = (totalPrice + totalFeeShip + fastDeliveryCost) * 1000; //based on cents
  const onToken = async (token) => {
    postCardPayment(currentUser, cartItems, methodDelivery, userMessage, priceForStripe / 1000)

  };
  return (
    <StripeCheckout
      name="Ecommerce"
      description={`Số tiền thanh toán ${totalPrice + totalFeeShip + fastDeliveryCost}`}
      image="https://svgshare.com/i/CUz.svg"
      panelLabel="Thanh toán ngay"
      amount={priceForStripe}
      currency="VND"
      locale="vi"
      stripeKey={publish_key}
      shippingAddress
      billingAddress
      zipCode={false}
      token={onToken}
      allowRememberMe
    />
  );
};
const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  cartItems: selectCartItems,
  totalPrice : selectTotalPrice, 
  totalFeeShip : selectTotalFeeShip,
  fastDeliveryCost : selectFastDeliveryCost,
  methodDelivery : selectMethodDelivery
});
export default connect(mapStateToProps, { clearCartItems })(StripeButton);
