import React, {useEffect} from 'react'
import {ProductTypeWrapper} from "./product-type.styles";
import {default as CategoryOverview} from "../../components/Category/category-overview/category-overview.container";
import MasterHeader from "../../components/Layout/master-header/master-header.component"
import Background from "../../components/Layout/background/background.component"
import {fetchProductType, fetchProductList} from "../../redux/product-type/product-type.actions";
import {connect} from "react-redux";
import {createStructuredSelector} from "reselect";
import {selectProductTypeError, selectProductTypeLoading , selectProductTypeList, selectProductFetched} from "../../redux/product-type/product-type.selectors"
import PageNotFound from "../page-not-found/page-not-found.component"
import Loader from "../../components/UI/loader/loader.component"
const ProductTypePage = ({match, location, fetchProductType, fetchProductList, loading, error, fetched}) => {
  useEffect(() => {
    let page = 1 ; 
    let {categoryUrl, productTypeUrl} = match.params ; 

    if(location.search && fetched){
      page = +location.search.split("=")[1];       
      fetchProductList(categoryUrl, productTypeUrl, page);
      return ; 
    }         
    fetchProductType(categoryUrl, productTypeUrl, page);

  }, [fetchProductType, fetchProductList, location.search, match.params.categoryUrl, match.params.productTypeUrl]);
    if(loading){
    return <Loader/>
    }
    if(error && error.status == 404){
    return <PageNotFound/>
    }
  return (
    <ProductTypeWrapper>
      ProductType
    </ProductTypeWrapper>
  )
}

const mapStateToProps = createStructuredSelector({
  error : selectProductTypeError,
  loading : selectProductTypeLoading,
  productTypeList : selectProductTypeList,
  fetched : selectProductFetched
})
const mapDispatchToProps = dispatch => ({
  fetchProductType : (path,page) => dispatch(fetchProductType(path,page)),
  fetchProductList : (path, page) => dispatch(fetchProductList(path, page))
})
export default connect(mapStateToProps, mapDispatchToProps)(ProductTypePage)
