import {createGlobalStyle} from "styled-components"

export const GlobalStyle = createGlobalStyle`
  @media screen and(min-width : 992px){
    :root{
      font-size : 16px;
    }
  }
  @media screen and(max-wdith: 992px){
    :root{
      font-size : 14px;
    }
  }
`
