import styled from "styled-components";

export const MasterSearchServiceContainer = styled.div`
  display : grid ; 
  grid-template-columns : 2fr 1fr;
  height : 100%;
  align-items : center;
  padding : 0 1rem;
`

export const SearchSection = styled.div`
  width : 75%;  
`

export const ServiceSection = styled.div`
  width : 25%;  
`
