import styled from "styled-components";

export const CartTableContainer = styled.div`
  width : 100%;
  display : flex ; 
  flex-direction : collumn;
`

export const TableHeader = styled.div`
  width : 100%;
  display : flex ; 
  padding : 1rem 0;
`

export const Row = styled.div`
  flex-grow : ${({product}) => product ? 2 : 1};
`
