import React, {useState} from 'react'
import clsx from 'clsx';
import { createStyles, makeStyles, useTheme, Theme } from '@material-ui/core/styles';
import {TaskBarContainer,Grid, Paragraph, Strong} from "./taskbar.styles";
import {getNumberOfProducts} from "../../../utils/algorithms";
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
      maxWidth: 300,
    },
    chips: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    chip: {
      margin: 2,
    },
    noLabel: {
      marginTop: theme.spacing(3),
    },
  }),
);

const TaskBar = () => {
  const classes = useStyles();
  const [sortBy, setSortBy] = useState("ascending")
  const numberOfProducts = getNumberOfProducts();
  const handleChange = (event) => {
    setSortBy(event.target.value);
  };
  return (
    <TaskBarContainer>
      <Grid>
        <Paragraph>Sort By</Paragraph>
        <FormControl className={classes.formControl}>
        <InputLabel id="demo-mutiple-name-label">Name</InputLabel>
        <Select
          labelId="demo-mutiple-name-label"
          id="demo-mutiple-name"
          multiple
          value={sortBy}
          onChange={handleChange}
          input={<Input />}         
        >

            <MenuItem value="ascending">
              Ascending
            </MenuItem>
            <MenuItem value="descending">
              Descending
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      </Grid>
      <Grid>
        <Paragraph>Found <Strong>{numberOfProducts} </Strong>products</Paragraph>
      </Grid>
      <Grid>
        Setting
      </Grid>
    </TaskBarContainer>
  )
}

export default TaskBar
