import React from "react";
import {
  LatestProductsContainer,
  Title,
  Grid,
  Slot,
  ProductImageContainer,
  ProductImage,
  ProductLabel,
  ProductName, 
  ProductPrice,
  OriginalPrice
} from "./latest-products.styles";
import { getLastestProducts } from "../../utils/algorithms";
import Slider from "react-slick";

let lastestProducts = getLastestProducts();
const LatestProducts = () => {
  let latestProductsGroup = [];
  let accumulatorProducts = [];
  console.log(lastestProducts)
  lastestProducts.forEach( (product,idx) => {
    if( (idx !== 0 && idx % 3 === 0 )  ){
      latestProductsGroup.push(accumulatorProducts);
      accumulatorProducts = [];
    }    
    accumulatorProducts.push(product)
    if( idx %3 !==0 && idx === lastestProducts.length -1 ){
      latestProductsGroup.push(accumulatorProducts);
      accumulatorProducts = [];
    }
  })
  console.log(latestProductsGroup)
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,    
    slidesToShow: 1,
    slidesToScroll: 1,  
    nextArrow : null ,
    prevArrow : null ,
    adaptiveHeight : true  
  };

  return (
    <LatestProductsContainer>
      <Title>sản phẩm gần đây</Title>
      <Slider {...settings} style={{height: "85%"}}>
        {latestProductsGroup.map((groups, id) => (
          <Grid key={id}>
            {groups.map((product) => (
              <Slot key={product.userId}>
                <ProductImageContainer>
                  <ProductImage src={product.imageUrl}/>
                </ProductImageContainer>
                <ProductLabel>
                   <ProductName >{product.name}</ProductName> 
                   {product.discount == 0 ? 
                    <OriginalPrice>{product.price.toLocaleString("es-AR")} Đ</OriginalPrice> : 
                    (
                      <React.Fragment>
                          <ProductPrice>{(product.price*(100-product.discount)/100).toLocaleString("es-AR")} Đ</ProductPrice>
                      </React.Fragment>
                    )
                   }
                </ProductLabel>
              </Slot>
            ))}
          </Grid>
        ))}
      </Slider>
    </LatestProductsContainer>
  );
};

export default LatestProducts;
