import React, {useState} from 'react'
import {TaskBarContainer,Grid, Paragraph, Strong, Select,Option} from "./taskbar.styles";
import {getNumberOfProducts} from "../../../utils/algorithms";

const TaskBar = () => {
  const numberOfProducts = getNumberOfProducts();
  const [sortBy, setSortBy] = useState("ascending");
  return (
    <TaskBarContainer>
      <Grid>
        <Paragraph>Sắp xếp</Paragraph>
        <Select value={sortBy}>
          <Option value="ascending">Tăng dần</Option>
          <Option value="descending">Giảm dần</Option>
        </Select>
      </Grid>
      <Grid>
        <Paragraph>Tìm thấy <Strong>{numberOfProducts} </Strong>products</Paragraph>
      </Grid>
      <Grid>

      </Grid>
    </TaskBarContainer>
  )
}

export default TaskBar
