import React, { useState } from "react";
import {
  ProductImagesContainer,
  BackgroundImageContainer,
  Image,
} from "./product-images.styles";
import Zoom from "react-img-zoom";
import Slider from "react-slick";
import {
  Magnifier,
  GlassMagnifier,
  SideBySideMagnifier,
  PictureInPictureMagnifier,
  MOUSE_ACTIVATION,
  TOUCH_ACTIVATION,
  MagnifierZoom,
  MagnifierContainer,
  MagnifierPreview,
} from "react-image-magnifiers";

const images = [
  require("../../../assets/img/blog/blog-1.jpg"),
  require("../../../assets/img/blog/blog-2.jpg"),
  require("../../../assets/img/blog/blog-3.jpg"),
  require("../../../assets/img/blog/blog-4.jpg"),
  require("../../../assets/img/blog/blog-5.jpg"),
  require("../../../assets/img/blog/blog-6.jpg"),
];

const ProductImages = () => {
  const [currentImg, setCurrentImg] = useState(images[0]);
  state = {
    allowOverflow: true,
    magnifierBorderSize: 5,
    magnifierBorderColor: "rgba(255, 255, 255, .5)",
    magnifierSize: "30%",
    square: false
  };

  handleSize = e => {
    const value = e.target.value + "%";
    this.setState(() => ({ magnifierSize: value }));
  };

  handleBorderSize = e => {
    const value = Number(e.target.value);
    this.setState(() => ({ magnifierBorderSize: value }));
  };

  handleBoolChange = key => e => {
    const value = Boolean(e.target.value);
    this.setState(() => ({ [key]: value }));
  };

  handleTextChange = key => e => {
    const value = e.target.value;
    this.setState(() => ({ [key]: value }));
  };
  React.useEffect(() => {
    console.log(currentImg);
  }, [currentImg]);
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplay: true,
    speed: 2000,
    autoplaySpeed: 2000,
    cssEase: "linear",
  };
  return (
    <ProductImagesContainer>
      <BackgroundImageContainer>

      </BackgroundImageContainer>
      <Slider {...settings}>
        {images.map((image, index) => (
          <Image key={image} src={image} onClick={() => setCurrentImg(image)} />
        ))}
      </Slider>
    </ProductImagesContainer>
  );
};

export default ProductImages;
