import styled from "styled-components";
export const BannerOverViewContainer = styled.section`
  display : flex ;   
  height :  ${({smallView}) => smallView ? "auto" : "50rem"} ;
  width : 100%;
  margin : ${({smallView}) => smallView ? "1rem auto" : "0 0 0 auto"};
  padding : 0.5rem;  

`

export const Grid = styled.div`
  width : ${({w20}) => w20 ? "20%" : "80%"};
  height: 100%;
`
