import React, {useEffect, memo, useState} from "react";
import {
  ProductItemContainer,
  ProductImageContainer,
  ProductImage,
  ProductInfo,
  ProductName,
  OriginalPrice,
  ProductDiscountPrice,
  DiscountBrand
} from "./product-item.styles";
import {timeCountDown} from "../../../utils/algorithms";
import Rating from "@material-ui/lab/Rating";
import Box from "@material-ui/core/Box";
import Chip from '@material-ui/core/Chip';
const ProductItem = ({ product }) => {
  const [endDiscountDate, setEndDiscountDate] = useState(null);
  const [timerDiscount, setTimerDiscount] = useState(null)
  useEffect(() => {
    let timerInterval ;   
    if(product.discount.end_at){
      timerInterval = setInterval(() => {
        let {dates,timeString} = timeCountDown(product.discount.end_at);
        setTimerDiscount(timeString);
        setEndDiscountDate(dates)
      },1000)      
    }   
    return () => clearInterval(timerInterval);
  },[])

  return (
    <ProductItemContainer key={product.userId} title={product.name}>
      <ProductImageContainer>
        <ProductImage src={`data:${product.images[0].mimetype};base64,${product.images[0].data}`} />
        <Box component="fieldset" mb={3} borderColor="transparent">
          <Rating
            name="read-only"
            value={product.stars}
            precision={0.5}
            readOnly
          />
        </Box>
      </ProductImageContainer>
      <ProductInfo>
        <ProductName>{product.name}</ProductName>
        {+product.discount.value === 0 ? (
          <OriginalPrice>
            {product.price.toLocaleString("es-AR")} ƒê
          </OriginalPrice>
        ) : (
          <React.Fragment>
            <OriginalPrice discount>
              {product.price.toLocaleString("es-AR")} ƒê
            </OriginalPrice>
            <ProductDiscountPrice>
              {(
                (product.price * (100 - parseFloat(product.discount.value))) /
                100
              ).toLocaleString("es-AR")}{" "}
              ƒê
            </ProductDiscountPrice>
          </React.Fragment>
        )}      
      </ProductInfo>
      {product.discount.value > 0 && (
        <React.Fragment>
          <DiscountBrand>{-product.discount.value}%</DiscountBrand>         
          {endDiscountDate? (
            endDiscountDate < 7 ? 
              <Chip  
              size="small"             
              label={timerDiscount}   
              color="secondary"  
              />         
             : 
              <Chip  
              size="small"             
              label={timerDiscount}   
              color="primary"  
            />
          ) : null }
        </React.Fragment>        
      )}

    </ProductItemContainer>
  );
};

export default memo(ProductItem);
