import { put, takeLatest, call, all, delay } from "redux-saga/effects";
import {
  registerSuccess,
  registerFail,
  fetchUserSuccess,
  fetchUserStart,
  fetchUserFail,
} from "./user.actions";
import userActionTypes from "./user.types";
import axios from "axios";
import urls from "../../utils/urls";
import setAuthToken from "../../utils/setAuthToken";

function* checkAuthTimeout(userExpDate) {
  yield delay(userExpDate * 1000);
  yield put(logout());
  yield call(setAuthToken, null);
}

function* logout() {
  localStorage.removeItem("userToken");
  localStorage.removeItem("userExpDate");
  yield put(logoutSuccess());
}

function* register({ payload: { name, email, password } }) {
  try {
    const {
      data: { token, user, expDate },
    } = yield axios.post(urls.REGISTER_URL, { name, email, password });

    localStorage.setItem("userToken", token);
    localStorage.setItem("userExpDate", new Date(Date.now() + expDate * 1000));
    setAuthToken(token);
    yield put(registerSuccess(user));
  } catch (error) {
    yield put(registerFail(error.message));
  }
}

function* fetchUser() {
  try {
    if (!localStorage.token || !localStorage.expDate) {
      throw new Error("Authentication denied");
    }
    if (
      !localStorage.expDate ||
      Date.parse(localStorage.expDate) < Date.now()
    ) {
      localStorage.removeItem("userExpDate");
      localStorage.removeItem("userToken");
      throw new Error("Token has expired");
    }
    setAuthToken(localStorage.token);
    const { data } = yield axios.get("/auth/user");
    yield put(fetchUserSuccess(data));
    yield put(checkAuthTimeout());
  } catch (error) {
    yield put(fetchUserFail(error.message));
  }
}

function* onRegister() {
  yield takeLatest(userActionTypes.REGISTER_START, register);
}

function* onLogout() {
  yield takeLatest(userActionTypes.LOGOUT_START, logout);
}

export default function* userSaga() {
  yield all([call(onRegister), call(onLogout)]);
}
