import {createGlobalStyle} from "styled-components"

export default createGlobalStyle`
  *{
    padding : 0; 
    margin : 0 ; 
    box-sizing: border-box; 
  }
  body{
    font-family : Roboto, "sans serif";
    font-size : 16px ; 
    line-height : 1.6;    
    @media screen and (max-width: 800px){
      font-size : 14px;
    }
`;
