import categoryActionTypes from "./category.types";

const INITIAL_STATE = {
  productTypeList: [],
  discountProductList: [],
  topRatedProducts: [],
  bestSellerProducts: [],
  numProducts: null,
  numPages: null,
  currentPage: 1,
  error: undefined,
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case categoryActionTypes.FETCH_CATEGORY_LIST_START:
      return {
        ...state,
        error: undefined,
        loading: true,
      };
    case categoryActionTypes.FETCH_CATEGORY_LIST_SUCCESS:
      return {
        ...state,
        productTypeList: action.payload.productTypeList,
        discountProductList: action.payload.discountProductList,
        topRatedProducts: action.payload.topRatedProducts,
        bestSellerProducts: action.payload.bestSellerProducts,
        productList: action.payload.productList,
        numProducts: +action.payload.numProducts,
        numPages: +action.payload.numPages,
        loading: false,
      };
    case categoryActionTypes.FETCH_CATEGORY_LIST_FAIL:
      return {
        ...state,
        error: action.payload,
        loading: false,
      };
    default:
      return state;
  }
};
