import React, {useState, useEffect, useRef} from 'react'
import {TaskbarContentWrapper} from "./task-content-item.styles";
const TaskbarContent = ({content, show}) => {
  const contentRef = useRef(null);
  const [text, setText] = useState(null);  
  useEffect(() => {            
    const lines = content.split(/<h1>|<h2>|<h3>|<h4>|<h5>|<h6>|<p>|<li>|<div>|/)   
    console.log(contentRef.style.scrollHeight)
  }, [content]) 
  return (
    <TaskbarContentWrapper dangerouslySetInnerHTML={{__html : content}} ref={contentRef}>

    </TaskbarContentWrapper>
  )
}

export default TaskbarContent
