import React from "react";
import { OrderItemContainer, ItemName, ItemPrice, ItemImage } from "./ordered-item.styles";
import { CSSTransition } from "react-transition-group";
import "./ordered-item.css";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
const OrderedItem = ({item, show, timeout }) => {
  console.log(item)
  console.log(show, timeout);
  return (
    <CSSTransition in={show} timeout={timeout} unmountOnExit classNames="alert">    
      <OrderItemContainer>
        <ItemImage src={`data:${item.image.mimetype};base64,${item.image.data}`}/>
        <ItemName>{item.name}</ItemName>
        <ItemPrice>${item.price * item.quantity}</ItemPrice>

      </OrderItemContainer>

    </CSSTransition>
  );
};

export default OrderedItem;
