import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Select,
  Option,
} from "./register-form.styles";
import AppContext from "../../../context/app-viewport.context";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCurrentUser } from "../../../redux/user/user.selectors";
import { usePaymentInputs } from "react-payment-inputs";
const RegisterForm = ({ user }) => {
  const {
    meta,
    getCardNumberProps,
    getExpiryDateProps,
    getCVCProps,
  } = usePaymentInputs();
  const [cardNumber, setCardNumber] = useState("");
  const [expiryDate, setExpiryDate] = useState("");
  const [cvc, setCvc] = useState("");
  const [payMethod, setPayMethod] = useState(null);
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);
  return (
    <Form mobileView={mobileView} tabletView={tabletView}>
      <FormInline>
        <FormGroup>
          <Label htmlFor="first_name">
            Họ<Required>*</Required>
          </Label>
          <Input name="first_name" id="first_name" />
        </FormGroup>
        <FormGroup>
          <Label htmlFor="last_name">
            Tên<Required>*</Required>
          </Label>
          <Input name="last_name" id="last_name" />
        </FormGroup>
      </FormInline>
      <FormGroup>
        <Input name="city" placeholder="Thành phố" />
      </FormGroup>
      <FormGroup>
        <Input name="address" placeholder="Quận Huyện" />
      </FormGroup>

      <FormGroup>
        <Input name="apartment" placeholder="Đường, Phường Xã, Số nhà" />
      </FormGroup>
      <FormInline>
        <FormGroup>
          <Label htmlFor="phone">Số điện thoại</Label>
          <Input name="phone1" id="phone1" />
        </FormGroup>
        <FormGroup>
          <Label>Email</Label>
          <Input name="email" value={user.email} disabled />
        </FormGroup>
      </FormInline>
      <Select
        defaultValue=""
        required
        onChange={(e) => setPayMethod(e.target.value)}
      >
        <Option disabled hidden>
          Phương thức thanh toán
        </Option>
        <Option value="cod">Tiền mặt (COD)</Option>
        <Option value="paypal">Thanh toán qua thẻ</Option>
      </Select>
      {payMethod === "paypal" && (
        <React.Fragment>
          <input
            {...getCardNumberProps({ onChange: (e) => setCardNumber(e.target.value) })}
            value={cardNumber}
          />
          <input
            {...getExpiryDateProps({ onChange: (e) => setExpiryDate(e.target.value) })}
            value={expiryDate}
          />
          <input {...getCVCProps({ onChange: (e) => setCvc(e.target.value) })} value={cvc} />
          {meta.isTouched && meta.error && <span>Error: {meta.error}</span>}
        </React.Fragment>
      )}
    </Form>
  );
};
const mapStateToProps = createStructuredSelector({
  user: selectCurrentUser,
});
export default connect(mapStateToProps)(RegisterForm);
