import React, {useEffect, useState} from 'react'
import {CommentReviewsItemWrapper, Row, Grid, Image,CommentText} from "./comment-reviews-item.styles";
import Moment from 'react-moment';
import {TiTick} from "react-icons/ti"
const CommentReviewsItem = ({comment, product}) => {
  const [readMore, setReadMore] = useState(false);  
  console.log(product)
  return (
    <CommentReviewsItemWrapper>      
      <Row>      
        <Image src={comment.user.avatar === "avatar-default.png" ? `http://localhost:5000/images/${comment.user.avatar}` : null}/>    
        <CommentText>
          <p><strong>{comment.user.name}{comment.user._id === product.user._id ? <span>Nhà bán hàng <TiTick/></span> : "" }</strong> <span style={{color: "rgba(0,0,0,0.75)"}}><Moment format="DD-MM-YYYY HH:mm">{comment.createdAt}</Moment></span> </p>
        {comment.text}
        </CommentText>


      </Row>
    </CommentReviewsItemWrapper>
  )
}

export default CommentReviewsItem
