import React, {useEffect, useRef, useState} from 'react'
import {CarouselBannerWrapper} from "./carousel-banners.styles";
import bannersData from "../../../data/banners"
import Banner from "../banner/banner.component";
import Slider from "react-slick";
const CustomArrowPrev = (props) => {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "white", padding: "0.5rem", opacity : "0.8" , position : "absolute", left: "0%", zIndex: 1}}
      onClick={onClick}
    />
  )
}
const CustomArrowNext = (props) => {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "white",padding: "0.5rem", opacity : "0.8" , position : "absolute", right: "0", zIndex: 1 }}
      onClick={onClick}
    />
  )
}

const CarouselBanner = () => {
  const [carouselHeight, setCarouselHeight] = useState(0)
  const carouselRef = useRef(null);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <CustomArrowNext />,
    prevArrow: <CustomArrowPrev />
  };

  useEffect(() => {
    setCarouselHeight(carouselRef.current.clientHeight);
  },[carouselRef.current] )
  return (
    <CarouselBannerWrapper  ref={carouselRef}>
      <Slider {...settings} >
        {bannersData.map(bannerItem => (
          <Banner key={bannerItem.imageUrl} img={bannerItem.imageUrl} height={carouselHeight}/>
        ))}
      </Slider>
    </CarouselBannerWrapper>
  )
}

export default CarouselBanner
