import React, {useState, useEffect} from "react";
import { MasterHeaderContainer, Grid } from "./master-header.styles";
import CategoryOverview from "../category-overview/category-overview.component";
import MasterService from "../master-service/master-service.component";
import MasterSearch from "../master-search/master-search.component";
import useWindowSize from "../../UI/useWindowSize/useWindowSize.util";
const MasterHeader = () => {
  const [width] = useWindowSize();
  const [isMobile, setIsMobile] = useState(width < 800);

  useEffect(() => {
    if(width < 800){
      setIsMobile(true);
    }else{
      setIsMobile(false);
    }
  }, [width])

  return (
    <MasterHeaderContainer isMobile>
      <Grid w25 isMobile>
        <CategoryOverview />
      </Grid>
      <Grid w50 isMobile>
        <MasterSearch />
      </Grid>
      <Grid w25 isMobile>
        <MasterService />
      </Grid>
    </MasterHeaderContainer>
  );
};

export default MasterHeader;
