import React, { useState } from "react";
import {
  ProductImagesContainer,
  BackgroundImageContainer,
  Image,
} from "./product-images.styles";
import Zoom from "react-img-zoom";
import Slider from "react-slick";
import {
  Magnifier,
  GlassMagnifier,
  SideBySideMagnifier,
  PictureInPictureMagnifier,
  MOUSE_ACTIVATION,
  TOUCH_ACTIVATION,
  MagnifierZoom,
  MagnifierContainer,
  MagnifierPreview,
} from "react-image-magnifiers";

const images = [
  require("../../../assets/img/blog/blog-1.jpg"),
  require("../../../assets/img/blog/blog-2.jpg"),
  require("../../../assets/img/blog/blog-3.jpg"),
  require("../../../assets/img/blog/blog-4.jpg"),
  require("../../../assets/img/blog/blog-5.jpg"),
  require("../../../assets/img/blog/blog-6.jpg"),
];

const ProductImages = () => {
  const [currentImg, setCurrentImg] = useState(images[0]);
  React.useEffect(() => {
    console.log(currentImg);
  }, [currentImg]);
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplay: true,
    speed: 2000,
    autoplaySpeed: 2000,
    cssEase: "linear",
  };
  return (
    <ProductImagesContainer>
      <BackgroundImageContainer>
        <MagnifierContainer>
          <div className="example-class">
            <MagnifierPreview imageSrc="./image.jpg" />
          </div>
          <MagnifierZoom style={{ height: "400px" }} imageSrc="./image.jpg" />
        </MagnifierContainer>
      </BackgroundImageContainer>
      <Slider {...settings}>
        {images.map((image, index) => (
          <Image key={image} src={image} onClick={() => setCurrentImg(image)} />
        ))}
      </Slider>
    </ProductImagesContainer>
  );
};

export default ProductImages;
