import categoryActionTypes from "./category.types";

export const fetchCategoryStart = (path, page = 1) => {
  return {
    type: categoryActionTypes.FETCH_CONTENT_LIST_BY_CATEGORY_PATH_URL_START,
    payload: { path, page },
  };
};
export const fetchCategorySuccess = ({
  productTypeList,
  discountProductList,
  topRatedProducts,
  bestSellerProducts,
  productList,
  numProducts,
  numPages,
}) => ({
  type: categoryActionTypes.FETCH_CONTENT_LIST_BY_CATEGORY_PATH_URL_SUCCESS,
  payload: {
    productTypeList,
    discountProductList,
    topRatedProducts,
    bestSellerProducts,
    productList,
    numProducts,
    numPages,
  },
});
export const fetchCategoryFail = (err) => {
  return {
    type: categoryActionTypes.FETCH_CONTENT_LIST_BY_CATEGORY_PATH_URL_FAIL,
    error: { msg: err.response.data.message, status: err.response.statusCode },
  };
};
