import React, { useState, useEffect } from "react";
import { PaginationContainer, PageLink } from "./right-side-pagination.styles";
import { withRouter } from "react-router-dom";
import { setCurrentPage } from "../../../redux/category/category.actions";
import { connect } from "react-redux";
import { generatePagination, listNextExtends, listPrevExtends } from "../../../utils/algorithms"
const Pagination = ({
  currentPage,
  numPages,
  match,
  history,
  location,
  setCurrentPage,
}) => {
  const [listPages, setListPages] = useState([]);
  let trackMaxListPage = 0;
  let trackMinListPage = 0 ;
  useEffect(() => {
    let listPages = generatePagination(currentPage, numPages);
    setListPages([...listPages]);
  }, [currentPage, numPages])

  const movePage = page => {
    const pathName = location.pathname.split("/")[1];
    history.push(`/${pathName}/products?page=${page}`);
    setCurrentPage(page);
  }
  const handlePageLink = (page) => {    
    if(page === "nextExtends"){
      let list = listNextExtends(listPages, currentPage, numPages)
      return setListPages([...list])       
    }else if(page === "prevExtends"){
      let list = listPrevExtends(listPages, currentPage, numPages)
      return setListPages([...list])   
    }
    movePage(page);
  };
  const handleNextPage = e => {
    movePage(currentPage + 1 );
  }
  return (
    <PaginationContainer>
      <PageLink>Về trước</PageLink>
      {listPages.length &&
        listPages.map((page) => (
          <PageLink  onClick={() => handlePageLink(page.to)}>{page.text}</PageLink>
        ))}
      <PageLink onClick={handleNextPage}>Tiếp</PageLink>
    </PaginationContainer>
  );
};

const mapDispatchToProps = dispatch => ({
  setCurrentPage : page => dispatch(setCurrentPage(page))
})
export default connect(null, mapDispatchToProps)(withRouter(Pagination));
