import React, { useState } from "react";
import { CustomCarousel, CarouselItem } from "./categories-slider.styles";
import categoriesData from "../../data/category";
import Carousel from "react-bootstrap/Carousel";
const CategoriesSlider = () => {
  const categoryItems = Object.keys(categoriesData).map(
    (key) => categoriesData[key]
  );

  let categoriesGroup = [];
  let categoriesPerPage = [];
  categoryItems.forEach((item, index) => {
    if (index !== 0 && index % 4 === 0) {
      categoriesGroup.push(categoriesPerPage);
      categoriesPerPage = [];
    }
    categoriesPerPage.push(item);
    if (index === categoryItems.length - 1 && index % 4 !== 0) {
      categoriesGroup.push(categoriesPerPage);
    }
  });
  console.log(categoriesGroup);
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  return (
    <CustomCarousel>
      <CarouselItem>
        {categoriesGroup.map((group) =>
          <CarouselItems>
            group.map((category) => (
              <div style={{ width: "150px", height: "150px" }}>
                <img
                  className="d-block w-100"
                  src={require(`../../assets/img${category.imageUrl}`)}
                  alt="First slide"
                  style={{ width: "150px", height: "180px" }}
                />

              </div>
            ))
          </CarouselItems>
        )}
      </CarouselItem>
    </CustomCarousel>
  );
};

export default CategoriesSlider;
