import React, { useState } from "react";
import { PaginationContainer, PageLink } from "./right-side-pagination.styles";
import { withRouter } from "react-router-dom";
import { setCurrentPage } from "../../../redux/category/category.actions";
import { connect } from "react-redux";
import { generatePagination } from "../../../utils/algorithms"
const Pagination = ({
  currentPage,
  numPages,
  match,
  history,
  location,
  setCurrentPage,
}) => {

  let trackMaxListPage = 0;
  let trackMinListPage = 0 ;
  let listPages = generatePagination(currentPage, numPages);

  const handlePageLink = (page) => {
    const pathName = location.pathname.split("/")[1];
    history.push(`/${pathName}/products?page=${page}`);
    setCurrentPage(page);
  };
  return (
    <PaginationContainer>
      <PageLink>Về trước</PageLink>
      {listPages.length &&
        listPages.map((page) => (
          <PageLink  onClick={() => handlePageLink(page.to)}>{page.text}</PageLink>
        ))}
      <PageLink to="/?page=5">Tiếp</PageLink>
    </PaginationContainer>
  );
};

const mapDispatchToProps = dispatch => ({
  setCurrentPage : page => dispatch(setCurrentPage(page))
})
export default connect(null, mapDispatchToProps)(withRouter(Pagination));
