import React, {useState} from "react";
import {
  ProductImagesContainer,
  BackgroundImageContainer,
  Image,
} from "./product-images.styles";
import Zoom from 'react-img-zoom'
const images = [
  "https://images-na.ssl-images-amazon.com/images/I/61wumn0iZSL._SY445_.jpg",
  "https://i.pcmag.com/imagery/reviews/04R1s9xuQfmVH4MHFeuaghc-18..v_1570065414.jpg",
  "https://zdnet4.cbsistatic.com/hub/i/r/2019/10/04/291e3a93-c814-4453-be01-9f1396a00b56/resize/570x428/d4ea7cbfa5758306e2b3ad7af93b1d29/totallee-iphone-11-pro-4.jpg",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRZOUonLVWQ8CTNrFPc_CxJ-pgVU-jsFQ04EQ&usqp=CAU",
  "https://store.storeimages.cdn-apple.com/8756/as-images.apple.com/is/MX0H2_AV4?wid=1144&hei=1144&fmt=jpeg&qlt=80&op_usm=0.5,0.5&.v=1566761841726",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRooHKHQczqodj5MUCQGcdX_6BfOpS9D5QzEA&usqp=CAU"
];

const ProductImages = () => {
  const [currentImg, setCurrentImg] = useState(images[0]);
  return (
    <ProductImagesContainer>
      <BackgroundImageContainer>
        <Zoom img={currentImg} zoomScale={2} width={300} height={500}/>
      </BackgroundImageContainer>
    </ProductImagesContainer>
  );
};

export default ProductImages;
