import React from "react";
import {
  LatestProductsContainer,
  Title,
  Grid,
  Slot,
  ProductImageContainer,
  ProductImage,
  ProductLabel,
  ProductName,
  ProductDiscountPrice,
  OriginalPrice,
} from "./products-top-rated.styles";
import { getProductsTopRated } from "../../utils/algorithms";
import Rating from "@material-ui/lab/Rating";
import Box from "@material-ui/core/Box";
import Tooltip from "@material-ui/core/Tooltip";
import Slider from "react-slick";

let productsTopRated = getProductsTopRated();
console.log(productsTopRated);
let productTopRatedGroup = [];
let accumulatorProducts = [];
productsTopRated.forEach((product, idx) => {
  if (idx !== 0 && idx % 3 === 0) {
    productTopRatedGroup.push(accumulatorProducts);
    accumulatorProducts = [];
  }
  accumulatorProducts.push(product);
  if (idx % 3 !== 0 && idx === productsTopRated.length - 1) {
    productTopRatedGroup.push(accumulatorProducts);
    accumulatorProducts = [];
  }
});

const ProductsTopRated = () => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: null,
    prevArrow: null,
    adaptiveHeight: true,
    autoplay: true,
  };

  return (
    <LatestProductsContainer>
      <Title>Top rate</Title>
      <Slider {...settings} style={{ height: "85%" }} autoplaySpeed={3000}>
        {productTopRatedGroup.map((groups, id) => (
          <Grid key={id}>
            {groups.map((product) => (
              <Slot key={product.userId}>
                <Tooltip title={product.name} placement="bottom">
                  <ProductImageContainer>
                    <ProductImage src={product.imageUrl} />
                    <Box component="fieldset" mb={3} borderColor="transparent">
                      <Rating
                        name="read-only"
                        value={product.votes}
                        precision={0.5}
                        readOnly
                      />
                    </Box>
                  </ProductImageContainer>
                  <ProductLabel>
                    <ProductName>{product.name}</ProductName>
                    {product.discount === 0 ? (
                      <OriginalPrice>
                        {product.price.toLocaleString("es-AR")} Đ
                      </OriginalPrice>
                    ) : (
                      <React.Fragment>
                        <OriginalPrice discount>
                          {product.price.toLocaleString("es-AR")} Đ
                        </OriginalPrice>
                        <ProductDiscountPrice>
                          {(
                            (product.price * (100 - product.discount)) /
                            100
                          ).toLocaleString("es-AR")}{" "}
                          Đ
                        </ProductDiscountPrice>
                      </React.Fragment>
                    )}
                  </ProductLabel>
                </Tooltip>
              </Slot>
            ))}
          </Grid>
        ))}
      </Slider>
    </LatestProductsContainer>
  );
};

export default ProductsTopRated;
