import React from 'react'
import {ResponseItemWrapper, Avatar, Row} from "./response-item.styles";
const ReponseItem = ({response}) => {
  return (
    <ResponseItemWrapper>
      <Row>
        <Avatar src={`http://localhost:5000/images/${response.user.avatar}`} />
      </Row>
    </ResponseItemWrapper>
  )
}

export default ReponseItem
