import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Select,
  Required,
  Option,
  BtnRemove
} from "./form-create-product.styles";
import AppContext from "../../../context/app-viewport.context";
import {
  getCategoryList,
  getProductsListByCategoryId,
} from "../../../utils/algorithms";

const categoryList = getCategoryList();


const FormCreateProduct = () => {
  const [category, setCategory] = useState({
    _id: "",
    name: "",
    linkUrl: "",
    products: [],
  });
  const [productTypesUrl, setProductTypesUrl] = useState("");
  const [addProduct, setAddProduct] = useState(false);
  const [newProductTypeName, setNewProductTypeName] = useState(null);
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);

  const handleCategoryChange = e => {    
    const category = categoryList.find(item => item._id === e.target.value);
    setCategory(category)
  }

  const handleChangeProductTypes = e => {
    if(e.target.value === "add-product"){
      return setAddProduct(true);
    }
    setProductTypesUrl("/" + e.target.value);
  }

  const handleRemoveAddNewProductType = e => {
    setAddProduct(false);
    setNewProductTypeName(null);
    productTypesUrl("");
  }

  return (
    <Form mobileView={mobileView} tabletView={tabletView}>
      <FormGroup>
        <Label>Nhóm sản phẩm</Label>
        <Select onChange={handleCategoryChange} >  
          <Option value="#">Lựa chọn sản phẩm</Option>
          {categoryList.map( (item) => (
            <Option key={item._id} value={item._id}>{item.name}</Option>
          ))}
        </Select>
      </FormGroup>
      <FormGroup>
        <Label>Loại sản phẩm</Label>
        <Select onChange={handleChangeProductTypes} disabled={!category.products.length || addProduct}>  
          <Option value="#">Lựa chọn sản phẩm</Option>
          {category.products && category.products.map( (item) => (
            <Option key={item} value={item}>{item}</Option>
          ))}
          <Option value="add-product">Lựa chọn sản phẩm khác</Option>
        </Select>
      </FormGroup>
      {addProduct && <FormGroup>
        <Label>Thêm Sản phẩm</Label>
        <Input type="text" name="addNewProductType" value={newProductTypeName} onChange={(e) => setNewProductTypeName(e.target.value)} />
        <BtnRemove onClick={ handleRemoveAddNewProductType}>&times;</BtnRemove>
      </FormGroup>}

    </Form>
  );
};

export default FormCreateProduct;
