import React, {useState} from "react";
import { SteppersContainer, StepperContent } from "./steppers.styles";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import RegisterForm from "../register-form/register-form.component";
import TermsAndPrivacy from "../terms-and-privacy/term-and-privacy.component";
import FormCreateProduct from "../form-create-product/form-create-product.component";
import {connect} from "react-redux"
import {selectCreateProductForm, selectRegisterForm} from "../../../redux/seller/seller.selectors";
import {createStructuredSelector} from "reselect";
import {registerAsSeller} from "../../../utils/algorithms";
import Loader from "../../UI/loader/loader.component";
const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
  },
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

function getSteps() {
  return [
    "Đăng ký kinh doanh",
    "Điều khoản và chính sách",
    "Đăng bán sản phẩm",
  ];
}

function getStepContent(stepIndex, disabledNext, setDisabledNext) { 

  switch (stepIndex) {
    case 0:
      return <RegisterForm disabledNext={disabledNext} setDisabledNext={setDisabledNext}/>;
    case 1:
      return <TermsAndPrivacy disabledNext={disabledNext} setDisabledNext={setDisabledNext}/>;
    case 2:
      return <FormCreateProduct disabledNext={disabledNext} setDisabledNext={setDisabledNext}/>;
    default:
      return "Unknown stepIndex";
  }
}

const Steppers = ({mobileView, tabletView, register, product}) => {

  const classes = useStyles();
  const [activeStep, setActiveStep] = useState(0);
  const [disabledNext, setDisabledNext] = useState(true)
  const [loading, setLoading] = useState(false);
  const steps = getSteps();

  const handleNext = async () => {  
    if(activeStep<steps.length-1){
      setActiveStep((prevActiveStep) => prevActiveStep + 1);
    }else{
      setLoading(true);
      const response = await registerAsSeller(register);
      console.log(response)
    }
  };


  const handleReset = () => {
    setActiveStep(0);
  };
  if(loading){
    return <Loader/>
  }
  return (
    <SteppersContainer>
      <div className={classes.root}>
        <Stepper activeStep={activeStep} alternativeLabel>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>
        <div>
          {activeStep === steps.length ? (
            <div>
              <Typography className={classes.instructions}>
                All steps completed
              </Typography>
              <Button onClick={handleReset}>Reset</Button>
            </div>
          ) : (
            <div>
              <StepperContent>
                {getStepContent(activeStep,disabledNext, setDisabledNext)}
              </StepperContent>
              <div style={{display: "flex", justifyContent : "center", margin: "1rem auto"}}>                
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleNext}
                  disabled={!disabledNext}                 
                >
                  {activeStep === steps.length - 1 ? "Hoàn tất" : "Tiếp theo"}
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </SteppersContainer>
  );
};

const mapStateToProps = createStructuredSelector({
  register : selectRegisterForm,
  product : selectCreateProductForm
})

export default connect(mapStateToProps)(Steppers);
