import {CustomLink} from "../../UI/custom-link/custom-link.component";
import NavigationItems from "../navigation-items/navigation-items.component";
import useWindowSize from "../../UI/useWindowSize/useWindowSize.util";
import Brand from "../navigation-brand/navigation-brand.component";
import Widget from "../navigation-widget/navigation-widget.component";
import {} from "../toggle-drawer/toggle-drawer.component";
const Header = () => {
  const [width, height] = useWindowSize();
  if(width > 800) 
  // for Desktop
    return (
      <DesktopContainer>
        <LeftSide>
          <Brand/>
        </LeftSide>
        <MidSide>
          <NavigationItems/>
        </MidSide>
        <RightSide>        
          <Widget/>
        </RightSide>
      </DesktopContainer>
    )
  return (
  //for Tablets, mobile
    <SmallerViewPort>

    </SmallerViewPort>
  )
}

export default Header
