import React,  { useState, useEffect, useContext } from 'react'
import {SellerOverviewContainer} from "./seller-overview.styles";
import Steppers from "../steppers/steppers.component";
import AppContext from "../../../context/app-viewport.context";
const SellerOverview = () => {
  const [payMethod, setPayMethod] = useState(null);
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(window.innerWidth < 992 && window.innerWidth >= 660);
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);
  return (
    <SellerOverviewContainer>
      <Steppers/>
    </SellerOverviewContainer>
  )
}

export default SellerOverview
