import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Select,
  Option,
  Editable,
} from "../../UI/custom-form-second/custom-form-second.component";
import {
  FormCreateProductWrapper,
  CustomNumberFormat,
} from "./form-create-product.styles";
import AppContext from "../../../context/app-viewport.context";
import { getListCategory, getListProductType } from "../../../utils/algorithms";
import ChipInput from "material-ui-chip-input";
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
const FormCreateProduct = () => {
  const [listCatogory, setListCategory] = useState([]);
  const [listProductType, setListProductType] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState({
    _id: "",
    name: "",
    linkUrl: "",
  });
  const [selectedProductType, setSelectedProductType] = useState({
    _id: "",
    name: "",
    linkUrl: "",
  });
  const [name, setName] = useState("");
  const [tags, setTags] = useState([]);
  const [price, setPrice] = useState(null);
  const [isDiscount, setIsDiscount] = useState(false);
  const [discount, setDiscount] = useState(null);

  const [error, setError] = useState(null);
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);

  useEffect(() => {
    let _mounted = true;
    getListCategory()
      .then((data) => {
        setListCategory(data);
      })
      .catch((err) => setError(err));
    return () => (_mounted = false);
  }, [getListCategory]);

  const handleChangeCategory = (e) => {
    const index = e.target.selectedIndex;
    const _id = e.target.value;
    const linkUrl = e.target.childNodes[index].dataset.url;
    const name = e.target[index].text;
    setSelectedCategory({ _id, name, linkUrl });
    getListProductType(_id)
      .then((data) => {
        setListProductType(data);
      })
      .catch((err) => setError(err));
  };

  const handleChangeChip = (chip) => {
    setTags(chip);
  };

  const handleChangePrice = (value) => {
    let fValue = value.floatValue;
    setPrice(fValue);
  };
  const handleChangeDiscount = (value) => {
    let fValue = value.floatValue;
    setDiscount(fValue);
  };

  return (
    <FormCreateProductWrapper>
      <Form>
        <FormGroup>
          <Select
            defaultValue={selectedCategory._id}
            onChange={handleChangeCategory}
          >
            <Option value="" disabled>
              -- Nhóm sản phẩm --
            </Option>
            {listCatogory.map((category) => (
              <Option
                key={category._id}
                value={category._id}
                data-url={category.linkUrl}
              >
                {category.name}
              </Option>
            ))}
          </Select>
        </FormGroup>
        <FormGroup>
          <Select defaultValue={selectedProductType._id}>
            <Option value="" disabled>
              -- Loại sản phẩm --
            </Option>
            {listProductType.map((productType) => (
              <Option
                key={productType._id}
                value={productType._id}
                data-url={productType.linkUrl}
              >
                {productType.name}
              </Option>
            ))}
          </Select>
        </FormGroup>
        <FormGroup>
          <Label>Tên Sản phẩm</Label>
          <Input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label>Tạo tags</Label>
          <ChipInput
            placeholder="#abc"
            fullWidth
            defaultValue={tags}
            newChipKeyCodes={[32]}
            onChange={(chip) => handleChangeChip(chip)}
          />
        </FormGroup>
        <FormGroup>
          <Label>Giá sản phẩm</Label>
          <CustomNumberFormat
            value={price}
            allowLeadingZeros={false}
            thousandSeparator={true}
            suffix={" Đ"}
            allowNegative={false}
            allowEmptyFormatting={true}
            onValueChange={handleChangePrice}
          />
        </FormGroup>
        <FormInline>
          <FormControlLabel
            control={
              <Checkbox
                checked={isDiscount}
                onChange={e => setIsDiscount(e.target.checked)}
                name="checkedB"
                color="primary"
              />
            }
            label="SP giảm giá"
          />
          <FormGroupAnimation>
            <Label isDiscount={isDiscount}>Khuyến mãi</Label>
            <CustomNumberFormat              
              value={discount}
              allowLeadingZeros={false}
              thousandSeparator={true}
              allowNegative={false}
              allowEmptyFormatting={true}
              onValueChange={handleChangeDiscount}
              suffix={" %"}
              isAllowed={({ floatValue }) =>
                floatValue < 100 && floatValue >= 0
              }
            />
          </FormGroupAnimation>
        </FormInline>
      </Form>
    </FormCreateProductWrapper>
  );
};

export default FormCreateProduct;
