import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Option,
} from "./form-create-product.styles";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import NativeSelect from "@material-ui/core/NativeSelect";
import AppContext from "../../../context/app-viewport.context";
import {
  getCategoryList,
  getProductsListByCategoryId,
} from "../../../utils/algorithms";
import category from "../../../data/category";

const categoryList = getCategoryList();

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 250,
    width: "100%",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const FormCreateProduct = () => {
  const classes = useStyles();
  const [payMethod, setPayMethod] = useState(null);
  const [categoryName, setCategoryName] = useState();
  const [categoryId, setCategoryId] = useState(null);
  const [categoryUrl, setCategoryUrl] = useState("");
  const [productsGroup, setProductsGroup] = useState([]);
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  useEffect(() => {
    if(categoryId){
      let category = categoryList.filter(item => item._id === categoryId);
      setCategoryName(category.name);
      setCategoryUrl(category.linkUrl);
      setProductsGroup(category.products);
    }
  }, [categoryId])

  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);

  const handleChangeCategory = categoryId => {       
    setCategoryId(categoryId)
  };
  return (
    <Form mobileView={mobileView} tabletView={tabletView}>
      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="age-native-helper">Loại Sản phẩm</InputLabel>
        <NativeSelect
          value={categoryName}
          onChange={(e) => handleChangeCategory(e.target.value)}
          inputProps={{
            name: "category",
            id: "category-native-helper",
          }}
        >
          <option aria-label="None" value="" />
          {categoryList.map((categoryItem) => (
            <option key={categoryItem._id} value={categoryItem._id}>
              {categoryItem.name}
            </option>
          ))}
        </NativeSelect>
      </FormControl>
      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="age-native-helper">Nhóm Sản phẩm</InputLabel>
        <NativeSelect
          value={category}        
          inputProps={{
            name: 'products',
            id: 'products-native-helper',
          }}
          disabled={!!categoryUrl}
        >
          <option aria-label="None" value="" />
          {productsGroup.map((groupName) => (
            <option key={groupName} value={groupName}>{groupName}</option>
          ))}
        </NativeSelect>    
      </FormControl>
      <FormGroup>
        <Label>
          Tên<Required>*</Required>
        </Label>
        <Input name="lname" />
      </FormGroup>
      <FormGroup>
        <Label>
          Quốc gia<Required>*</Required>
        </Label>
        <Input name="country" />
      </FormGroup>
      <FormGroup>
        <Input name="address" placeholder="Đường, Phường/Xã, Quận/Huyện" />
      </FormGroup>
      <FormGroup>
        <Input name="city" placeholder="Thành phố" />
      </FormGroup>
      <FormGroup>
        <Input name="apartment" placeholder="Số nhà" />
      </FormGroup>
      <FormInline>
        <FormGroup>
          <Label>Số điện thoại</Label>
          <Input name="phone" />
        </FormGroup>
        <FormGroup>
          <Label>Email</Label>
          <Input name="email" />
        </FormGroup>
      </FormInline>

      {payMethod === "paypal" && (
        <React.Fragment>
          <FormGroup>
            <Label>Số Thẻ</Label>
            <Input name="paypalId" />
          </FormGroup>
          <FormGroup>
            <Label>Tên chủ thẻ</Label>
            <Input name="ownerPaypal" />
          </FormGroup>
        </React.Fragment>
      )}
    </Form>
  );
};

export default FormCreateProduct;
