import styled from "styled-components";
import {lighten} from "polished"
export const CategoryToggleContainer = styled.div`
  display : flex ; 
  padding : .4rem 1rem;
  background-color : rgb(127,173,57);
  color : white;
  text-align:center;
  &:hover{
    cursor : pointer;
    background-color : lighten(0.5, white)         
  }
`

export const CategoryLeft = styled.div`
  flex : 1 0 20%;
  display : flex ; 
  flex-direction : column;
  justify-content: space-around;
  align-items : center;

`
export const Slash = styled.div`
  width : 1.25rem;
  height : .15rem;
  background-color : white;
`
export const CategoryMiddle = styled.div`
  flex : 2 0 60%;  
`
export const CategoryRight = styled.div`
  flex : 1 0 20%;
`
