import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Select,
  Required,
  Option,
} from "./form-create-product.styles";
import {
  getCategoryList,
  getProductsListByCategoryId,
} from "../../../utils/algorithms";

const categoryList = getCategoryList();


const FormCreateProduct = () => {
  const classes = useStyles();
  const [category, setCategory] = useState({
    _id: "",
    name: "",
    linkUrl: "",
    products: [],
  });
  const [productsGroup, setProductsGroup] = useState([]);
  const [productsType, setProductsType] = useState("");
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);

  const handleCategory = (e) => {
    const  category  =categoryList.find(
      (item) => item._id === e.target.value
    );
    if (category) {
      setCategory({
        ...category,
        _id: category._id,
        name: category.name,
        linkUrl: category.linkUrl,
        products: category.products,
      });
    } else {
      setCategory({ _id: "", name: "", linkUrl: "", products: [] });
    }
  };
  console.log(category)
  return (
    <Form mobileView={mobileView} tabletView={tabletView}>
      <FormGroup>
        <Label>Loại sản phẩm</Label>
        <Select>
          <Option value="" disabled>Lựa chọn loại sản phẩm</Option>
          {categoryList.map( (item) => (
            <Option key={item._id} value={item._id}>{item.name}</Option>
          ))}
        </Select>
      </FormGroup>
    </Form>
  );
};

export default FormCreateProduct;
